    let b: Vec<usize> = lines.next().unwrap().unwrap().split_whitespace().map(|s| s.parse::<usize>().unwrap()).collect();

    let mut max_ratio = 0;
    let mut count = 0;
    for i in 0..n {
        for j in 0..m {
            let ratio = a[i] as f64 / b[j] as f64;
            if ratio.fract() == 0.0 {
                if ratio > max_ratio {
                    max_ratio = ratio;
                    count = 1;
                } else if ratio == max_ratio {
                    count += 1;
                }
            }
        }
    }

    println!("{}", count);
}
