        a = append(a, ai)
        b = append(b, bi)
    }
    sort.Ints(a)
    sort.Ints(b)
    var best, worst int
    for i := 0; i < n; i++ {
        if a[i]+b[i] >= x {
            best = i + 1
            break
        }
    }
    for i := n - 1; i >= 0; i-- {
        if a[i]+b[i] >= x {
            worst = i + 1
            break
        }
    }
    fmt.Printf(" %d %d ", best, worst)
}

