        for (int i = l; i <= r - k + 1; i++) {
            int temp = fib[i + k - 1];
            for (int j = i; j < i + k; j++) {
                temp = temp % m * fib[j] % m;
            }
            gcd = gcd(gcd, temp);
        }

        System.out.println(gcd % m);
    }

    private static int gcd(int a, int b) {
        if (b == 0) {
            return a;
        }
        return gcd(b, a % b);
    }
}
