    scanf(" %d %d ", &n, &m);

    Node[] nodes;
    for (int i = 0; i < n; i++) {
        Node node;
        node.id = i + 1;
        node.time = 0;
        node.indegree = 0;
        node.outdegree = 0;
        node.visited = false;
        node.edges = [];
        nodes.push(node);
    }

    for (int i = 0; i < m; i++) {
        int a, b, c;
        scanf(" %d %d %d ", &a, &b, &c);

        Edge edge;
        edge.from = a;
        edge.to = b;
        edge.time = c;

        nodes[a - 1].edges.push(edge);
        nodes[b - 1].edges.push(edge);
    }

    int k;
    scanf(" %d ", &k);

    int[] times;
    for (int i = 0; i < k; i++) {
        int t;
        scanf(" %d ", &t);
        times.push(t);
    }

