        front++;

        nodes[current].visited = true;

        for (int i = 0; i < nodes[current].edges.length; i++) {
            int next = nodes[current].edges[i].to - 1;

            if (!nodes[next].visited) {
                nodes[next].time = max(nodes[current].time, nodes[current].edges[i].time);
                nodes[next].indegree--;

