fun main() {
    val (n, k) = readLine().split(" ").map { it.toInt() }
    val a = readLine().split(" ").map { it.toInt() }.sorted()
    val m = a.distinct().sorted()
    val dp = Array(n + 1) { IntArray(m.size + 1) { 0 } }
    dp[0][0] = 1
    for (i in 1..n) {
        for (j in 0..m.size) {
            dp[i][j] = dp[i - 1][j]
            if (a[i] == m[j]) {
                dp[i][j]++
            } else if (a[i] < m[j]) {
                dp[i][j] = maxOf(dp[i][j], dp[i - 1][j])
            } else {
                dp[i][j] = maxOf(dp[i][j], dp[i - 1][j - 1] + 1)
            }
        }
    }
    val ans = dp.last().max()
    val idx = dp.last().indexOf(ans)
    println("$ans ${m[idx]}")
}
