public class Main {

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        String str = scanner.nextLine();
        int n = str.length();
        int ans = 0;
        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++) {
                String sub = str.substring(i, j + 1);
                int count = 0;
                for (int k = 0; k < n; k++) {
                    if (str.contains(sub)) {
                        count++;
                    }
                }
                if (count >= 2) {
                    ans = Math.max(ans, sub.length());
                }
            }
        }
        System.out.println(ans);
    }
}
