s = input()

def longest_substring(s):
    n = len(s)
    dp = [[0] * n for _ in range(n)]
    for i in range(n):
        for j in range(i + 1, n):
            if s[i] == s[j]:
                dp[i][j] = dp[i - 1][j - 1] + 1
            else:
                dp[i][j] = 0

    return max(dp[i][j] for i in range(n) for j in range(i + 1, n))

print(longest_substring(s))
""

