import std.stdio;
import std.algorithm;

void main() {
    string s;
    scanf(" %s ", s);
    int n = s.length;
    int dp[n][n];
    int ans = 0;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            if (i == j) {
                dp[i][j] = 1;
            } else {
                dp[i][j] = 0;
            }
        }
    }
    for (int i = 1; i < n; i++) {
        for (int j = 0; j < n - i; j++) {
            if (s[j] == s[j + i]) {
                dp[j][j + i] = dp[j + 1][j + i - 1] + 1;
                ans = max(ans, dp[j][j + i]);
            }
        }
    }
    printf(" %d ", ans);
}

