my $str = <STDIN>;
my $len = length($str);
my $max = 0;
my $cur = 0;
my $prev = 0;

for (my $i = 0; $i < $len; $i++) {
    if ($str[$i] eq $str[$prev]) {
        $cur++;
    } else {
        $cur = 1;
    }

    if ($cur > $max) {
        $max = $cur;
    }

    $prev = $i;
}

print $max;

exit 0;

