n = int(input())

def get_divisors(n):
    divisors = []
    for i in range(1, int(n ** 0.5) + 1):
        if n % i == 0:
            divisors.append(i)
            divisors.append(n // i)
    return divisors

def get_smallest_integer_with_n_divisors(n):
    divisors = get_divisors(n)
    return min(divisors)

