using System;
using System.Collections.Generic;
using System.Linq;

class Program
{
    static void Main(string[] args)
    {
        var n, k = int.Parse(Console.ReadLine());
        var x = Console.ReadLine().Split(' ').Select(int.Parse).ToArray();
        var y = int.Parse(Console.ReadLine());

        var dp = new double[n + 2, n + 2];
        for (int i = 0; i <= n + 1; i++)
        {
            for (int j = 0; j <= n + 1; j++)
            {
                dp[i, j] = double.MaxValue;
            }
        }

        dp[0, 0] = 0;
        for (int i = 0; i <= n + 1; i++)
        {
            for (int j = 0; j <= n + 1; j++)
            {
                if (i == 0 && j == 0)
                {
                    continue;
                }

                dp[i, j] = Math.Min(dp[i, j], dp[i - 1, j] + Math.Sqrt(Math.Pow(x[i] - x[i - 1], 2) + Math.Pow(y[i] - y[i - 1], 2)));
                dp[i, j] = Math.Min(dp[i, j], dp[i, j - 1] + Math.Sqrt(Math.Pow(x[j] - x[j - 1], 2) + Math.Pow(y[j] - y[j - 1], 2)));
            }
        }

        Console.WriteLine(dp[k, n + 1]);
    }
}

