	}
	for i := 0; i < n; i++ {
		y[i+1] = y[i] + y[i+1]
	}
	ans := math.Inf(1)
	for i := 0; i < n; i++ {
		for j := 0; j < n; j++ {
			for l := 0; l < n; l++ {
				for m := 0; m < n; m++ {
					if i == j || i == l || i == m || j == l || j == m || l == m {
