s = input()

def is_valid(s):
    if len(s) < 3:
        return False
    if s[0] == '@' or s[-1] == '@':
        return False
    for i in range(1, len(s) - 1):
        if s[i] == '@':
            return False
    return True

def solve(s):
    res = []
    i = 0
    while i < len(s):
        while i < len(s) and not is_valid(s[i:]):
            i += 1
        if i == len(s):
            break
        res.append(s[i:i + s.find('@')])
        i += s.find('@') + 1
    return ','.join(res)

print(solve(s))
""

