            for (var j = 0; j <= m - s; j++)
            {
                var count = 0;
                for (var x = i; x < i + s; x++)
                {
                    for (var y = j; y < j + s; y++)
                    {
                        if (x == i || x == i + s - 1 || y == j || y == j + s - 1)
                        {
                            count++;
                        }
                    }
                }
                ans = Math.Max(ans, count);
            }
        }

        Console.WriteLine(ans);
    }
}

