import std.stdio;
import std.algorithm;
import std.math;

struct Point {
    int x;
    int y;

    Point(int x, int y) {
        this.x = x;
        this.y = y;
    }

    Point operator +(Point p) {
        return Point(this.x + p.x, this.y + p.y);
    }

    Point operator -(Point p) {
        return Point(this.x - p.x, this.y - p.y);
    }

    int dot(Point p) {
        return this.x * p.x + this.y * p.y;
    }

    int cross(Point p) {
        return this.x * p.y - this.y * p.x;
    }

    int dist(Point p) {
        return sqrt(this.dot(p));
    }
}

struct Segment {
    Point p1;
    Point p2;

    Segment(Point p1, Point p2) {
        this.p1 = p1;
        this.p2 = p2;
    }

    bool intersect(Segment s) {
        int d1 = (s.p2 - s.p1).cross(this.p1 - s.p1);
        int d2 = (s.p2 - s.p1).cross(this.p2 - s.p1);
        int d3 = (this.p2 - this.p1).cross(s.p1 - this.p1);
        int d4 = (this.p2 - this.p1).cross(s.p2 - this.p1);

        if (d1 * d2 < 0 && d3 * d4 < 0) {
            return true;
        }

        return false;
    }
}

int main() {
    Point v = Point(cast(int)stdin.readLine().strip().split(" ")[0].to!int, cast(int)stdin.readLine().strip().split(" ")[1].to!int);
    Point p = Point(cast(int)stdin.readLine().strip().split(" ")[0].to!int, cast(int)stdin.readLine().strip().split(" ")[1].to!int);
    Segment w = Segment(Point(cast(int)stdin.readLine().strip().split(" ")[0].to!int, cast(int)stdin.readLine().strip().split(" ")[1].to!int), Point(cast(int)stdin.readLine().strip().split(" ")[2].to!int, cast(int)stdin.readLine().strip().split(" ")[3].to!int));
    Segment m = Segment(Point(cast(int)stdin.readLine().strip().split(" ")[0].to!int, cast(int)stdin.readLine().strip().split(" ")[1].to!int), Point(cast(int)stdin.readLine().strip().split(" ")[2].to!int, cast(int)stdin.readLine().strip().split(" ")[3].to!int));

    if (w.intersect(m)) {
        return "NO";
    }

    Point v2 = v + (p - v).normalize() * 1000000;

    if (w.intersect(Segment(v, v2))) {
        return "NO";
    }

    if (m.intersect(Segment(v, v2))) {
        return "YES";
    }

    return "NO";
}
