        if (i < 0 || i >= a.length || j < 0 || j >= a[0].length || a[i][j] == 0) {
            return;
        }
        a[i][j] = 0;
        dfs(a, i - 1, j);
        dfs(a, i + 1, j);
        dfs(a, i, j - 1);
        dfs(a, i, j + 1);
    }
}
