            }
        }
    }
    println(ans)
}

fun dfs(i: Int, j: Int, a: Array<Array<Char>>) {
    if (i < 0 || i >= a.size || j < 0 || j >= a[0].size || a[i][j] == 'W') {
        return
    }
    a[i][j] = 'W'
    dfs(i - 1, j, a)
    dfs(i + 1, j, a)
    dfs(i, j - 1, a)
    dfs(i, j + 1, a)
}
