            System.out.println("the first player won");
        } else if (oCount == 3) {
            System.out.println("the second player won");
        } else if (xCount + oCount == 9) {
            System.out.println("draw");
        } else {
            if (checkWin(board, 'X')) {
                System.out.println("the first player won");
            } else if (checkWin(board, '0')) {
                System.out.println("the second player won");
            } else {
                if (xCount == oCount) {
                    System.out.println("second");
                } else {
                    System.out.println("first");
                }
            }
        }
    }

    private static boolean checkWin(String[] board, char player) {
        for (int i = 0; i < 3; i++) {
            if (board[i].charAt(0) == player && board[i].charAt(1) == player && board[i].charAt(2) == player) {
