	s := make([]byte, n)
	for i := 0; i < n; i++ {
		fmt.Scan(&s[i])
	}
	dp := make([]*big.Int, n+1)
	dp[0] = big.NewInt(1)
	for i := 1; i <= n; i++ {
		if s[i-1] == '?' {
			dp[i] = dp[i-1].Mul(dp[i-1], big.NewInt(3))
		} else if s[i-1] == '*' {
			dp[i] = big.NewInt(0)
		} else {
			dp[i] = dp[i-1].Mul(dp[i-1], big.NewInt(2))
		}
		dp[i] = dp[i].Mod(dp[i], big.NewInt(1000000007))
	}
	fmt.Println(dp[n].Int64())
}

