import (
    "fmt"
    "sort"
)

func main() {
    var n, a, b int
    fmt.Scan(&n, &a, &b)
    var a1, b1 []int
    for i := 0; i < a; i++ {
        var x int
        fmt.Scan(&x)
        a1 = append(a1, x)
    }
    for i := 0; i < b; i++ {
        var x int
        fmt.Scan(&x)
        b1 = append(b1, x)
    }
    sort.Ints(a1)
    sort.Ints(b1)
    var ans []int
    for i := 0; i < n; i++ {
        if i < a {
            ans = append(ans, 1)
        } else {
            ans = append(ans, 2)
        }
    }
    for i := 0; i < a; i++ {
        if ans[a1[i]-1] == 2 {
            ans[a1[i]-1] = 1
        }
    }
    for i := 0; i < b; i++ {
        if ans[b1[i]-1] == 1 {
            ans[b1[i]-1] = 2
        }
    }
    for i := 0; i < n; i++ {
        fmt.Print(ans[i], " ")
    }
    fmt.Println()
}

