#!/usr/bin/perl
use strict;
use warnings;

my $n, $a, $b;
my @a_apples, @b_apples;

chomp(my $input = <STDIN>);
($n, $a, $b) = split(' ', $input);

chomp(my $a_input = <STDIN>);
@a_apples = split(' ', $a_input);

chomp(my $b_input = <STDIN>);
@b_apples = split(' ', $b_input);

my @result;

for (my $i = 1; $i <= $n; $i++) {
    if (grep { $_ == $i } @a_apples) {
        push(@result, 1);
    } elsif (grep { $_ == $i } @b_apples) {
        push(@result, 2);
    } else {
        die "No apple for $i\n";
    }
}

print join(' ', @result);

exit 0;

