public class Main {

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        int n = scanner.nextInt();
        int m = scanner.nextInt();
        int k = scanner.nextInt();

        int[][] doors = new int[m][2];
        for (int i = 0; i < m; i++) {
            doors[i][0] = scanner.nextInt();
            doors[i][1] = scanner.nextInt();
        }

        String[][] people1 = new String[k][3];
        for (int i = 0; i < k; i++) {
            people1[i][0] = scanner.next();
            people1[i][1] = scanner.nextInt() + "";
            people1[i][2] = scanner.next();
        }

        String[][] people2 = new String[k][3];
        for (int i = 0; i < k; i++) {
            people2[i][0] = scanner.next();
            people2[i][1] = scanner.nextInt() + "";
            people2[i][2] = scanner.next();
        }

        int[] keys1 = new int[m];
        int[] keys2 = new int[m];
        for (int i = 0; i < k; i++) {
            for (int j = 0; j < people1[i][2].split(" ").length; j++) {
                int key = Integer.parseInt(people1[i][2].split(" ")[j]);
                keys1[key - 1]++;
            }
        }

        for (int i = 0; i < k; i++) {
            for (int j = 0; j < people2[i][2].split(" ").length; j++) {
                int key = Integer.parseInt(people2[i][2].split(" ")[j]);
                keys2[key - 1]++;
            }
        }

        for (int i = 0; i < m; i++) {
            if (keys1[i] != keys2[i]) {
                System.out.println("NO");
                return;
            }
        }

        int[][] graph = new int[n][n];
        for (int i = 0; i < m; i++) {
            graph[doors[i][0] - 1][doors[i][1] - 1] = 1;
            graph[doors[i][1] - 1][doors[i][0] - 1] = 1;
        }

        boolean[] visited = new boolean[n];
        for (int i = 0; i < k; i++) {
            if (!visited[people1[i][1] - 1]) {
                dfs(graph, people1[i][1] - 1, visited);
            }
        }

        for (int i = 0; i < k; i++) {
            if (!visited[people2[i][1] - 1]) {
                System.out.println("NO");
                return;
            }
        }

        System.out.println("YES");
    }

    private static void dfs(int[][] graph, int v, boolean[] visited) {
        visited[v] = true;
        for (int i = 0; i < graph.length; i++) {
            if (graph[v][i] == 1 && !visited[i]) {
                dfs(graph, i, visited);
            }
        }
    }
}
