import java.util.*

fun main() {
    val br = System.`in`.bufferedReader()
    val bw = System.out.bufferedWriter()

    val (n, m, k) = br.readLine().split(" ").map { it.toInt() }
    val doors = Array(m) { ArrayList<Int>() }
    for (i in 0 until m) {
        val (a, b) = br.readLine().split(" ").map { it.toInt() }
        doors[i].add(a)
        doors[i].add(b)
    }
    val people = Array(k) { ArrayList<Int>() }
    for (i in 0 until k) {
        val (name, room, keyNum) = br.readLine().split(" ")
        val keys = br.readLine().split(" ").map { it.toInt() }
        people[i].add(name)
        people[i].add(room.toInt())
        people[i].add(keyNum.toInt())
        for (j in 0 until keyNum) {
            doors[keys[j] - 1].add(room.toInt())
        }
    }

    val visited = Array(n) { false }
    val queue = LinkedList<Int>()
    queue.add(people[0][1])
    visited[people[0][1]] = true
    while (queue.isNotEmpty()) {
        val cur = queue.poll()
        for (door in doors[cur - 1]) {
            if (!visited[door]) {
                queue.add(door)
                visited[door] = true
            }
        }
    }

    val visited2 = Array(n) { false }
    queue.add(people[k - 1][1])
    visited2[people[k - 1][1]] = true
    while (queue.isNotEmpty()) {
        val cur = queue.poll()
        for (door in doors[cur - 1]) {
            if (!visited2[door]) {
                queue.add(door)
                visited2[door] = true
            }
        }
    }

    for (i in 0 until n) {
        if (visited[i] != visited2[i]) {
            bw.write("NO\n")
            return
        }
    }

    bw.write("YES\n")
    bw.flush()
}
