use std::collections::HashMap;

fn main() {
    // 读取输入
    let (n, m, k) = read_line().unwrap().split_whitespace().map(|x| x.parse().unwrap()).collect::<Vec<_>>();
    let mut doors = HashMap::new();
    for _ in 0..m {
        let (a, b) = read_line().unwrap().split_whitespace().map(|x| x.parse().unwrap()).collect::<Vec<_>>();
        doors.insert(a, b);
        doors.insert(b, a);
    }
    let mut people = HashMap::new();
    for _ in 0..k {
        let (name, room, k) = read_line().unwrap().split_whitespace().map(|x| x.parse().unwrap()).collect::<Vec<_>>();
        people.insert(name, (room, k));
    }

    // 判断是否可以从第一天到第二天
    let mut can_reach = vec![false; n + 1];
    for (name, (room, k)) in people.iter() {
        for i in 0..k {
            let door = read_line().unwrap().split_whitespace().map(|x| x.parse().unwrap()).collect::<Vec<_>>();
            if door[0] == room {
                can_reach[door[1]] = true;
            }
        }
    }

    // 判断是否可以从第二天到第一天
    let mut can_reach_back = vec![false; n + 1];
    for (name, (room, k)) in people.iter() {
        for i in 0..k {
            let door = read_line().unwrap().split_whitespace().map(|x| x.parse().unwrap()).collect::<Vec<_>>();
            if door[1] == room {
                can_reach_back[door[0]] = true;
            }
        }
    }

    // 输出结果
    if can_reach.iter().all(|&x| x) && can_reach_back.iter().all(|&x| x) {
        println!("YES");
    } else {
        println!("NO");
    }
}

fn read_line() -> String {
    let mut line = String::new();
    std::io::stdin().read_line(&mut line).unwrap();
    line
}
