fn main() {
    let stdin = io::stdin();
    let mut lines = stdin.lock().lines();

    let (n, m) = lines
        .next()
        .unwrap()
        .split_whitespace()
        .map(|s| s.parse::<usize>().unwrap())
        .collect::<(usize, usize)>();

    let mut dp = vec![vec![0; 6]; m + 1];
    dp[0][0] = 1;

    for i in 1..=m {
        let (s, c) = lines
            .next()
            .unwrap()
            .split_whitespace()
            .map(|s| s.parse::<usize>().unwrap())
            .collect::<(Vec<u8>, usize)>();

        for j in 0..6 {
            for k in 0..6 {
                if s[j] == '1' && k <= c {
                    dp[i][k + 1] += dp[i - 1][j];
                }
            }
        }
    }

    println!("{}", dp[m][6]);
}
