import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

public class Main {

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        String[] input = br.readLine().split(" ");
        int n = Integer.parseInt(input[0]);
        int m = Integer.parseInt(input[1]);
        List<List<Integer>> graph = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            graph.add(new ArrayList<>());
        }
        for (int i = 0; i < m; i++) {
            input = br.readLine().split(" ");
            int a = Integer.parseInt(input[0]);
            int b = Integer.parseInt(input[1]);
            graph.get(a - 1).add(b - 1);
            graph.get(b - 1).add(a - 1);
        }
        int[] visited = new int[n];
        int[] component = new int[n];
        int componentCount = 0;
        for (int i = 0; i < n; i++) {
            if (visited[i] == 0) {
                dfs(graph, visited, component, i, componentCount);
                componentCount++;
            }
        }
        int[] componentSize = new int[componentCount];
        for (int i = 0; i < n; i++) {
            componentSize[component[i]]++;
        }
        int ans = 0;
        for (int i = 0; i < componentCount; i++) {
            ans += componentSize[i] - 1;
        }
        System.out.println(ans);
    }

    private static void dfs(List<List<Integer>> graph, int[] visited, int[] component, int i, int componentId) {
        visited[i] = 1;
        component[i] = componentId;
        for (int j : graph.get(i)) {
            if (visited[j] == 0) {
                dfs(graph, visited, component, j, componentId);
            }
        }
    }
}
