}

fun dfs(graph: Array<MutableList<Int>>, v: Int, count: Int, k: Int): Int {
    if (count == k) {
        return 1
    }

    var ans = 0
    for (i in graph[v]) {
        if (i != v) {
            ans += dfs(graph, i, count + 1, k)
        }
    }

    return ans
}
