    for i := 1; i <= a; i++ {
        for j := 1; j <= b; j++ {
            if dp[i][j] > ans {
                ans = dp[i][j]
                x, y = i, j
            }
        }
    }
    fmt.Println(ans)
    fmt.Println(x, y)
