use std::collections::{HashMap, HashSet};

fn main() {
    // 读取输入
    let (n, m) = read_line().unwrap().split_whitespace().map(|x| x.parse().unwrap()).collect_tuple().unwrap();
    let mut railways = HashMap::new();
    for _ in 0..m {
        let (u, v) = read_line().unwrap().split_whitespace().map(|x| x.parse().unwrap()).collect_tuple().unwrap();
        railways.entry(u).or_insert(HashSet::new()).insert(v);
        railways.entry(v).or_insert(HashSet::new()).insert(u);
    }

    // 计算最短路径
    let mut distances = vec![std::usize::MAX; n + 1];
    let mut queue = Vec::new();
    queue.push((1, 0));
    distances[1] = 0;
    while !queue.is_empty() {
        let (u, d) = queue.pop().unwrap();
        for v in railways.get(&u).unwrap() {
            if distances[v] > d + 1 {
                distances[v] = d + 1;
                queue.push((v, d + 1));
            }
        }
    }

    // 输出结果
    if distances[n] == std::usize::MAX {
        println!("-1");
    } else {
        println!("{}", distances[n]);
    }
}

fn read_line() -> String {
    let mut line = String::new();
    std::io::stdin().read_line(&mut line).unwrap();
    line.trim().to_string()
}
