  n, a, b: longint;
  a1, a2: array[0..MAXN] of longint;
  dp: array[0..MAXN, 0..MAXN] of longint;

function gcd(a, b: longint): longint;
begin
  if b = 0 then
    gcd := a
  else
    gcd := gcd(b, a mod b);
end;

function solve(i, j: longint): longint;
begin
  if dp[i, j] <> -1 then
    exit(dp[i, j]);
  if i = j then
    return 0;
  if i > j then
    return solve(j, i);
  if a1[i] = a1[j] then
    return solve(i + 1, j - 1);
  dp[i, j] := min(solve(i + 1, j) + a, solve(i, j - 1) + a);
  for k := i + 1 to j - 1 do
