use std::collections::HashMap;

fn main() {
    let (n, k) = read_line().unwrap().split_whitespace().map(|x| x.parse().unwrap()).collect::<Vec<_>>();
    let a = read_line().unwrap().split_whitespace().map(|x| x.parse().unwrap()).collect::<Vec<_>>();

    let mut dp = HashMap::new();
    dp.insert((0, 0), 1);

    for i in 0..n {
        for (j, &b) in a.iter().enumerate() {
            if i == j {
                continue;
            }

            let (diff, sum) = (b - a[i], a[i] + b);
            let mut new_dp = HashMap::new();
            for (&k, &v) in dp.iter() {
                if k.0 + diff <= k.1 + sum && k.0 + diff <= k.1 + k.0 {
                    new_dp.insert((k.0 + diff, k.1 + sum), (v + new_dp.get(&(k.0 + diff, k.1 + sum)).unwrap_or(&0)) % 1000000007);
                }
                if k.0 + diff <= k.1 + k.1 {
                    new_dp.insert((k.0 + diff, k.1 + k.1), (v + new_dp.get(&(k.0 + diff, k.1 + k.1)).unwrap_or(&0)) % 1000000007);
                }
            }
            dp = new_dp;
        }
    }

