const getPossibleMoves = (board) => {
  const moves = [];
  for (let i = 0; i < 5; i++) {
    for (let j = 0; j < 19; j++) {
      if (board[i][j] === 'O') {
        const possibleMoves = getPossibleMovesInRow(board, i, j);
        moves.push(...possibleMoves);
      }
    }
  }
  return moves;
};

const getPossibleMovesInRow = (board, i, j) => {
  const moves = [];
  if (i > 0 && board[i - 1][j] === 'O') {
    moves.push([i - 1, j]);
  }
  if (i < 4 && board[i + 1][j] === 'O') {
    moves.push([i + 1, j]);
  }
  if (j > 0 && board[i][j - 1] === 'O') {
    moves.push([i, j - 1]);
  }
  if (j < 18 && board[i][j + 1] === 'O') {
    moves.push([i, j + 1]);
  }
  return moves;
};

const getWinner = (board) => {
  const moves = getPossibleMoves(board);
  if (moves.length === 0) {
    return 'Lillebror';
  } else {
    return 'Karlsson';
  }
};

