const fs = require('fs');
const input = fs.readFileSync('input.txt', 'utf8').split('\n');
const n = Number(input[0]);
const dates = input.slice(1).map(date => Number(date));

const sortedDates = dates.sort((a, b) => a - b);

const result = [];
for (let i = 0; i < n; i++) {
  const date = sortedDates[i];
  const nextDate = sortedDates[i + 1];

  if (date < 1000 || date > 2011) {
    result.push('No solution');
    continue;
  }

  if (date === nextDate) {
    result.push(date);
    continue;
  }

  const diff = nextDate - date;
  const digit = diff % 10;

  if (digit === 0) {
    result.push(date + 1);
  } else if (date % 10 === 0) {
    result.push(date + digit);
  } else {
    result.push(date - digit);
  }
}

fs.writeFileSync('output.txt', result.join('\n'));
