fun main() {
    val n = readLine()!!.toInt()
    val dates = Array(n) { readLine()!!.toInt() }
    val ans = Array(n) { 0 }
    val set = HashSet<Int>()
    val pq = PriorityQueue<Pair<Int, Int>>(compareBy { it.first })
    for (i in 0 until n) {
        pq.add(Pair(dates[i], i))
    }
    while (pq.isNotEmpty()) {
        val (date, idx) = pq.poll()
        if (set.contains(date)) {
            continue
        }
        set.add(date)
        ans[idx] = date
        for (i in 0..3) {
            for (j in 0..9) {
                val newDate = date + (j - date % 10) * 10 ** i
                if (newDate in 1000..2011 && !set.contains(newDate)) {
                    pq.add(Pair(newDate, idx))
                }
            }
        }
    }
    for (i in 0 until n) {
        print(ans[i])
        print("\n")
    }
}
