const
  maxn = 1000;

type
  TPoint = record
    x, y: integer;
  end;

var
  n: integer;
  a: array[0..maxn] of TPoint;
  s: array[0..maxn] of boolean;
  ans: integer;

procedure readin;
var
  i: integer;
begin
  readln(n);
  for i := 0 to n - 1 do
  begin
    readln(a[i].x, a[i].y);
  end;
end;

procedure solve;
var
  i, j, k: integer;
  l, r: integer;
begin
  for i := 0 to n - 1 do
  begin
    s[i] := false;
  end;
  s[0] := true;
  ans := 0;
  for i := 1 to n - 1 do
  begin
    l := 0;
    r := n - 1;
    while l < r do
    begin
      k := (l + r) div 2;
      if (a[k].x < a[i].x) then
        l := k + 1
      else
        r := k;
    end;
    if (a[r].y < a[i].y) then
      r := r - 1;
    for j := r + 1 to n - 1 do
    begin
      if (a[j].y > a[i].y) then
      begin
        if (a[j].x < a[i].x) then
          break;
        if (not s[j]) then
        begin
          s[j] := true;
          ans := ans + 1;
