    let mut xs = Vec::new();
    let mut ys = Vec::new();

    for _ in 0..n {
        let line = lines.next().unwrap().unwrap();
        let mut iter = line.split_whitespace();
        let x = iter.next().unwrap().parse::<i32>().unwrap();
        let y = iter.next().unwrap().parse::<i32>().unwrap();
        xs.push(x);
        ys.push(y);
    }

    let mut ans = 0;

    for i in 0..n {
        let x1 = xs[i];
        let y1 = ys[i];
        let x2 = xs[(i + 1) % n];
        let y2 = ys[(i + 1) % n];

        for j in 0..n {
            let x3 = xs[j];
            let y3 = ys[j];

            if x1 == x2 && x3 == x2 {
                continue;
            }

            let m = (y2 - y1) / (x2 - x1);
            let c = y1 - m * x1;

            let y = m * x3 + c;

            if y >= y3 && y <= y2 {
                ans += 1;
            }
        }
    }

    println!("{}", ans);
