import std.stdio;
import std.algorithm;
import std.array;

struct Point {
    int x;
    int y;

    Point(int x, int y) {
        this.x = x;
        this.y = y;
    }

    bool operator==(const Point& other) const {
        return x == other.x && y == other.y;
    }

    bool operator<(const Point& other) const {
        return x < other.x || (x == other.x && y < other.y);
    }
}

struct Triangle {
    Point a;
    Point b;
    Point c;

    Triangle(Point a, Point b, Point c) {
        this.a = a;
        this.b = b;
        this.c = c;
    }

    bool operator==(const Triangle& other) const {
        return a == other.a && b == other.b && c == other.c;
    }

    bool operator<(const Triangle& other) const {
        return a < other.a || (a == other.a && b < other.b) || (a == other.a && b == other.b && c < other.c);
    }
}

int main() {
    int n;
    scanf(" %d ", &n);

    Triangle[] triangles;
    for (int i = 0; i < n; i++) {
        int x1, y1, x2, y2, x3, y3;
        scanf(" %d %d %d %d %d %d ", &x1, &y1, &x2, &y2, &x3, &y3);
        triangles.push(Triangle(Point(x1, y1), Point(x2, y2), Point(x3, y3)));
    }

    int ans = 0;
    foreach (triangle in triangles) {
        int count = 0;
        foreach (other in triangles) {
            if (triangle != other && triangle.a == other.a || triangle.a == other.b || triangle.a == other.c) {
                count++;
            }
            if (triangle != other && triangle.b == other.a || triangle.b == other.b || triangle.b == other.c) {
                count++;
            }
            if (triangle != other && triangle.c == other.a || triangle.c == other.b || triangle.c == other.c) {
                count++;
            }
        }
        ans = max(ans, count);
    }

    printf(" %d ", ans);

    return 0;
}"

