import java.util.List;

public class Main {

    public static void main(String[] args) throws IOException {
        BufferedReader reader = new BufferedReader(new InputStreamReader(System.in));
        String[] input = reader.readLine().split(" ");
        int x = Integer.parseInt(input[0]);
        int y = Integer.parseInt(input[1]);
        int n = Integer.parseInt(input[2]);
        int d = Integer.parseInt(input[3]);
        List<int[]> vectors = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            input = reader.readLine().split(" ");
            int[] vector = new int[]{Integer.parseInt(input[0]), Integer.parseInt(input[1])};
            vectors.add(vector);
        }
        int[] anton = new int[]{x, y};
        int[] dasha = new int[]{x, y};
        boolean antonTurn = true;
        while (true) {
            if (antonTurn) {
                int[] nextVector = getBestVector(anton, vectors, d);
                anton = addVector(anton, nextVector);
                if (distance(anton, new int[]{0, 0}) > d) {
                    System.out.println("Dasha");
                    return;
                }
            } else {
                int[] nextVector = getBestVector(dasha, vectors, d);
                dasha = addVector(dasha, nextVector);
                if (distance(dasha, new int[]{0, 0}) > d) {
                    System.out.println("Anton");
                    return;
                }
            }
            antonTurn = !antonTurn;
        }
    }

    private static int[] getBestVector(int[] current, List<int[]> vectors, int d) {
        int bestDistance = Integer.MAX_VALUE;
        int[] bestVector = null;
        for (int[] vector : vectors) {
            int distance = distance(current, vector);
            if (distance < bestDistance && distance <= d) {
                bestDistance = distance;
                bestVector = vector;
            }
        }
        return bestVector;
    }

    private static int[] addVector(int[] current, int[] vector) {
        return new int[]{current[0] + vector[0], current[1] + vector[1]};
    }

    private static int distance(int[] a, int[] b) {
        return Math.abs(a[0] - b[0]) + Math.abs(a[1] - b[1]);
    }
}
