using System;
using System.Collections.Generic;
using System.Linq;

class Program
{
    static void Main(string[] args)
    {
        var input = Console.ReadLine().Split(' ');
        var x = int.Parse(input[0]);
        var y = int.Parse(input[1]);
        var n = int.Parse(input[2]);
        var d = int.Parse(input[3]);
        var vectors = new List<Vector>();
        for (int i = 0; i < n; i++)
        {
            var vector = new Vector(int.Parse(Console.ReadLine().Split(' ')[0]), int.Parse(Console.ReadLine().Split(' ')[1]));
            vectors.Add(vector);
        }

        var anton = new Player(x, y, d);
        var dasha = new Player(x, y, d);

        var winner = anton.Play(vectors, dasha);

        Console.WriteLine(winner);
    }

    class Vector
    {
        public int X { get; set; }
        public int Y { get; set; }

        public Vector(int x, int y)
        {
            X = x;
            Y = y;
        }
    }

    class Player
    {
        public int X { get; set; }
        public int Y { get; set; }
        public int D { get; set; }

        public Player(int x, int y, int d)
        {
            X = x;
            Y = y;
            D = d;
        }

        public bool CanMove(Vector vector)
        {
            return Math.Abs(X + vector.X) <= D && Math.Abs(Y + vector.Y) <= D;
        }

        public bool CanReflect()
        {
            return X == Y;
        }

        public Player Play(List<Vector> vectors, Player opponent)
        {
            var bestMove = vectors.FirstOrDefault(v => CanMove(v));
            if (bestMove != null)
            {
                return new Player(X + bestMove.X, Y + bestMove.Y, D);
            }
            else if (CanReflect())
            {
                return new Player(-X, -Y, D);
            }
            else
            {
                return opponent;
            }
        }
    }
}

