import std.stdio;
import std.algorithm;
import std.array;

struct Vector {
    int x, y;

    Vector(int x, int y) {
        this.x = x;
        this.y = y;
    }

    Vector operator +(Vector other) {
        return Vector(this.x + other.x, this.y + other.y);
    }

    Vector operator -(Vector other) {
        return Vector(this.x - other.x, this.y - other.y);
    }

    int dot(Vector other) {
        return this.x * other.x + this.y * other.y;
    }

    int cross(Vector other) {
        return this.x * other.y - this.y * other.x;
    }

    bool operator ==(Vector other) {
        return this.x == other.x && this.y == other.y;
    }
}

int main() {
    int x, y, n, d;
    scanf(" %d %d %d %d ", &x, &y, &n, &d);

    Vector[] vectors = new Vector[n];
    for (int i = 0; i < n; i++) {
        int x, y;
        scanf(" %d %d ", &x, &y);
        vectors[i] = Vector(x, y);
    }

    Vector current = Vector(x, y);
    Vector last = current;
    int count = 0;
    while (true) {
        if (current.dot(current) > d * d) {
            break;
        }

        Vector best = vectors[0];
        for (int i = 1; i < n; i++) {
            if (vectors[i].dot(current) > best.dot(current)) {
                best = vectors[i];
            }
        }

        if (best == last) {
            break;
        }

        last = current;
        current = current + best;
        count++;
    }

    if (count % 2 == 0) {
        printf("Anton\n");
    } else {
        printf("Dasha\n");
    }

    return 0;
}
""

