use std::collections::{HashMap, HashSet};

fn main() {
    let (n, k) = read_line().unwrap().split_whitespace().map(|x| x.parse().unwrap()).collect::<Vec<_>>();
    let u = read_line().unwrap().split_whitespace().map(|x| x.parse().unwrap()).collect::<Vec<_>>();
