    for i in 0..n - 1 {
        let (x, y) = read_line().unwrap().split_whitespace().map(|x| x.parse().unwrap()).collect::<Vec<_>>();
        g.entry(x).or_insert(Vec::new()).push(y);
        g.entry(y).or_insert(Vec::new()).push(x);
