    int n;
    cin >> n;
    vector<Point> p(n);
    for (int i = 0; i < n; i++) {
        int x, y;
        cin >> x >> y;
        p[i] = Point(x, y);
    }
    sort(p.begin(), p.end());
    vector<Point> hull;
    hull.push_back(p[0]);
    hull.push_back(p[1]);
    hull.push_back(p[2]);
    for (int i = 3; i < n; i++) {
        while (hull.size() >= 2 && ccw(hull[hull.size() - 2], hull[hull.size() - 1], p[i]) <= 0) {
            hull.pop_back();
        }
        hull.push_back(p[i]);
    }
    int m;
    cin >> m;
    for (int i = 0; i < m; i++) {
        int x, y;
        cin >> x >> y;
        Point q(x, y);
        bool in = false;
        for (int j = 0; j < hull.size(); j++) {
            if (j == hull.size() - 1) {
                if (ccw(hull[j], hull[0], q) > 0) {
                    in = true;
                }
            } else {
                if (ccw(hull[j], hull[j + 1], q) > 0) {
                    in = true;
                }
            }
        }
        if (in) {
            cout << "YES" << endl;
        } else {
            cout << "NO" << endl;
        }
    }
    return 0;
}

int ccw(Point a, Point b, Point c) {
    return (b.x - a.x) * (c.y - a.y) - (b.y - a.y) * (c.x - a.x);
