import java.util.Scanner;

public class Main {

    static class Point {
        int x, y;

        public Point(int x, int y) {
            this.x = x;
            this.y = y;
        }

        @Override
        public String toString() {
            return "(" + x + "," + y + ")";
        }
    }

    static class ConvexHull {
        Point[] points;
        int n;

        public ConvexHull(Point[] points) {
            this.points = points;
            this.n = points.length;
        }

        public boolean isInside(Point p) {
            int lo = 0, hi = n - 1;
            while (lo < hi) {
                int mid = (lo + hi) / 2;
                if (points[mid].y < p.y) {
                    lo = mid + 1;
                } else {
                    hi = mid;
                }
            }
            int cur = lo;
            while (cur < n && points[cur].y == p.y) {
                if (points[cur].x <= p.x) {
                    cur++;
                } else {
                    break;
                }
            }
            if (cur == n) {
                return false;
            }
            int next = cur + 1;
            while (next < n && points[next].y == p.y) {
                if (points[next].x <= p.x) {
                    next++;
                } else {
                    break;
                }
            }
            if (next == n) {
                return false;
            }
            return points[cur].x + (p.y - points[cur].y) / (points[next].y - points[cur].y) * (points[next].x - points[cur].x) <= p.x;
        }
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        Point[] points = new Point[n];
        for (int i = 0; i < n; i++) {
            points[i] = new Point(sc.nextInt(), sc.nextInt());
        }
        ConvexHull hull = new ConvexHull(points);
        for (int i = 0; i < n; i++) {
            System.out.println(hull.isInside(points[i]) ? "YES" : "NO");
        }
    }
}
