                    for (int j = 0; j < points.Count; j++)
                    {
                        Point p1 = points[j];
                        for (int k = j + 1; k < points.Count; k++)
                        {
                            Point p2 = points[k];
                            if (IsPointInTriangle(p1, p2, new Point(x, y)))
                            {
                                isInside = true;
                                break;
                            }
                        }
                    }
                    Console.WriteLine(isInside ? "YES" : "NO");
                }
            }
        }

        static bool IsPointInTriangle(Point p1, Point p2, Point p3)
        {
            int a1 = p2.y - p1.y;
            int b1 = p1.x - p2.x;
            int c1 = a1 * p1.x + b1 * p1.y;

            int a2 = p3.y - p1.y;
            int b2 = p1.x - p3.x;
            int c2 = a2 * p1.x + b2 * p1.y;

            int a3 = p3.y - p2.y;
            int b3 = p2.x - p3.x;
            int c3 = a3 * p2.x + b3 * p2.y;

            int d = a1 * b2 + b1 * c2 + c1 * a2 - a2 * b1 - b2 * c1 - c2 * a1;

            if (d == 0)
            {
                return false;
            }

            int x = (b2 * c3 - b3 * c2) / d;
            int y = (a3 * c1 - a1 * c3) / d;

            return x == p3.x && y == p3.y;
        }

