package main

import (
	"fmt"
	"math"
)

type Point struct {
	X, Y int
}

type Edge struct {
	A, B Point
}

func main() {
	var n int
	fmt.Scan(&n)

	points := make([]Point, n)
	for i := 0; i < n; i++ {
		var x, y int
		fmt.Scan(&x, &y)
		points[i] = Point{x, y}
	}

	hull := convexHull(points)

	for i := 0; i < n; i++ {
		if isInHull(hull, points[i]) {
			fmt.Println("YES")
		} else {
			fmt.Println("NO")
		}
	}
}

func convexHull(points []Point) []Edge {
	n := len(points)
	if n < 3 {
		return nil
	}

	// Sort points by x-coordinate, then by y-coordinate.
	sort.Slice(points, func(i, j int) bool {
		if points[i].X < points[j].X {
			return true
		} else if points[i].X == points[j].X {
			return points[i].Y < points[j].Y
		} else {
			return false
		}
	})

	// Find the lower hull.
	lowerHull := make([]Edge, 0, n)
	for i := 0; i < n; i++ {
		for len(lowerHull) >= 2 && orientation(lowerHull[len(lowerHull)-2], lowerHull[len(lowerHull)-1], points[i]) <= 0 {
			lowerHull = lowerHull[:len(lowerHull)-1]
		}
		lowerHull = append(lowerHull, Edge{points[i], points[(i+1)%n]})
	}

	// Find the upper hull.
	upperHull := make([]Edge, 0, n)
	for i := n - 1; i >= 0; i-- {
		for len(upperHull) >= 2 && orientation(upperHull[len(upperHull)-2], upperHull[len(upperHull)-1], points[i]) <= 0 {
			upperHull = upperHull[:len(upperHull)-1]
		}
		upperHull = append(upperHull, Edge{points[i], points[(i+1)%n]})
	}

	// Merge the lower and upper hulls.
	hull := make([]Edge, 0, len(lowerHull)+len(upperHull))
	hull = append(hull, lowerHull...)
	hull = append(hull, upperHull...)

	return hull
}

func orientation(a, b, c Point) int {
	return (b.X-a.X)*(c.Y-a.Y) - (b.Y-a.Y)*(c.X-a.X)
}

func isInHull(hull []Edge, p Point) bool {
	for i := 0; i < len(hull); i++ {
		if orientation(hull[i].A, hull[i].B, p) < 0 {
			return false
		}
	}
	return true
}
