import java.util.*

fun main() {
    val n = readLine().toInt()
    val points = mutableListOf<Pair<Int, Int>>()
    val hull = mutableListOf<Pair<Int, Int>>()
    val pq = PriorityQueue<Pair<Int, Int>>(compareBy { it.second })
    for (i in 0 until n) {
        val (t, x, y) = readLine().split(" ").map { it.toInt() }
        if (t == 1) {
            points.add(Pair(x, y))
            pq.add(Pair(x, y))
        } else {
            val ans = if (isInside(points, x, y)) "YES" else "NO"
            println(ans)
        }
    }
}

fun isInside(points: List<Pair<Int, Int>>, x: Int, y: Int): Boolean {
    val n = points.size
    val hull = convexHull(points)
    var i = 0
    var j = n - 1
    while (i < n && j >= 0) {
        val (xi, yi) = hull[i]
        val (xj, yj) = hull[j]
        if (cross(xi, yi, xj, yj, x, y) > 0) {
            return false
        }
        if (y < yi || (y == yi && x < xi)) {
            i++
        } else {
            j--
        }
    }
    return true
}

fun cross(x1: Int, y1: Int, x2: Int, y2: Int, x: Int, y: Int): Int {
    return (x2 - x1) * (y - y1) - (x - x1) * (y2 - y1)
}

fun convexHull(points: List<Pair<Int, Int>>): List<Pair<Int, Int>> {
    val n = points.size
    val hull = mutableListOf<Pair<Int, Int>>()
    val sorted = points.sortedBy { it.first }
    var i = 0
    var j = n - 1
    while (i < n && j >= 0) {
        val (xi, yi) = sorted[i]
        val (xj, yj) = sorted[j]
        if (cross(xi, yi, xj, yj, 0, 0) > 0) {
            hull.add(Pair(xi, yi))
            i++
        } else {
            hull.add(Pair(xj, yj))
            j--
        }
    }
    if (i == 0) {
        hull.add(Pair(sorted[n - 1].first, sorted[n - 1].second))
    }
    return hull
}
