import std.stdio;
import std.algorithm;
import std.array;
import std.math;

struct Point {
    int x, y;
    Point(int x, int y) {
        this.x = x;
        this.y = y;
    }
}

struct Line {
    Point a, b;
    Line(Point a, Point b) {
        this.a = a;
        this.b = b;
    }

    int cross(Point p) {
        return (b.x - a.x) * (p.y - a.y) - (b.y - a.y) * (p.x - a.x);
    }

    bool isLeft(Point p) {
        return cross(p) > 0;
    }

    bool isRight(Point p) {
        return cross(p) < 0;
    }

    bool onSegment(Point p) {
        return (a.x <= p.x && p.x <= b.x) && (a.y <= p.y && p.y <= b.y);
    }
}

struct ConvexHull {
    Point[] points;

    ConvexHull(Point[] points) {
        this.points = points;
    }

    bool isInside(Point p) {
        int n = points.length;
        if (n < 3)
            return false;

        int l = 0, r = n - 1;
        while (l < r) {
            int mid = (l + r) / 2;
            if (points[mid].isLeft(p))
                l = mid + 1;
            else
                r = mid;
        }

        if (points[l].isRight(p))
            return false;

        int m = l;
        for (int i = l + 1; i < n; i++) {
            if (points[i].isLeft(p))
                m = i;
        }

        return points[m].onSegment(p);
    }
}

int main() {
    int n;
    scanf(" %d ", &n);

    Point[] points;
    for (int i = 0; i < n; i++) {
        int x, y;
        scanf(" %d %d ", &x, &y);
        points.push(Point(x, y));
    }

    ConvexHull hull = ConvexHull(points);

    for (int i = 0; i < n; i++) {
        int x, y;
        scanf(" %d %d ", &x, &y);
        Point p = Point(x, y);

        if (hull.isInside(p))
            printf(" YES ");
        else
            printf(" NO ");
        printf(" 
 ");
    }

    return 0;
}
