import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.ArrayList;
import java.util.Arrays;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

public class Main {

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        String[] input = br.readLine().split(" ");
        int n = Integer.parseInt(input[0]);
        int m = Integer.parseInt(input[1]);
        int k = Integer.parseInt(input[2]);

        List<int[]> edges = new ArrayList<>();
        for (int i = 0; i < m; i++) {
            input = br.readLine().split(" ");
            int u = Integer.parseInt(input[0]);
            int v = Integer.parseInt(input[1]);
            edges.add(new int[]{u, v});
        }

        Map<Integer, List<Integer>> graph = new HashMap<>();
        for (int i = 0; i < n; i++) {
            graph.put(i, new ArrayList<>());
        }

        for (int[] edge : edges) {
            graph.get(edge[0]).add(edge[1]);
            graph.get(edge[1]).add(edge[0]);
        }

        int[] components = new int[n];
        int componentCount = 0;
        for (int i = 0; i < n; i++) {
            if (components[i] == 0) {
                dfs(graph, i, components, componentCount);
                componentCount++;
            }
        }

        int[] tunnels = new int[componentCount];
        for (int i = 0; i < m; i++) {
            int[] edge = edges.get(i);
            int component1 = components[edge[0]];
            int component2 = components[edge[1]];
            if (component1 != component2) {
                tunnels[component1]++;
                tunnels[component2]++;
            }
        }

        int answer = 0;
        for (int i = 0; i < componentCount; i++) {
            if (tunnels[i] > k) {
                answer += tunnels[i] - k;
            }
        }

        System.out.println(answer);
    }

    private static void dfs(Map<Integer, List<Integer>> graph, int u, int[] components, int componentCount) {
        components[u] = componentCount;
        for (int v : graph.get(u)) {
            if (components[v] == 0) {
                dfs(graph, v, components, componentCount);
            }
        }
    }
}
